package br.com.testwebserviceapi.ui;import androidx.appcompat.app.AppCompatActivity;import android.content.Intent;import android.os.Bundle;import android.widget.EditText;import br.com.testwebserviceapi.Domain.User;import br.com.testwebserviceapi.R;public class UpdateActivity extends AppCompatActivity {    private User user;    private String nome_ant;    private String sobrenome_ant;    private String email_ant;    private String senha_ant;    private String desc_ant;    private String data_nasc_ant;    private String sexo_ant;    private String tipo_ant;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_update);        this.user = getIntent().getParcelableExtra("dtupdt");        this.email_ant = user.getEmail();        this.senha_ant = user.getSenha();        EditText editTextName = (EditText) findViewById(R.id.edtNameUpdate);        EditText editTextLastName = (EditText) findViewById(R.id.edtLastNameUpdate);        EditText editTextEmail = (EditText) findViewById(R.id.edtEmailUpdate);        EditText editTextSenha = (EditText) findViewById(R.id.edtSenhaUpdate);        EditText editTextDesc = (EditText) findViewById(R.id.edtDescUpdate);        EditText editTextDateNasc = (EditText) findViewById(R.id.edtDateUpdate);        EditText editTextSexo = (EditText) findViewById(R.id.edtSexoUpdate);        editTextName.setText(user.getNome());        editTextLastName.setText(user.getSobrenome());        editTextEmail.setText(user.getEmail());        editTextSenha.setText(user.getSenha());        editTextDesc.setText(user.getDescricao());        editTextDateNasc.setText(user.getData());        editTextSexo.setText(user.getSexo());    }    /*@Override    public void onBackPressed() {        super.onBackPressed();        Intent intent  = new Intent(UpdateActivity.this, HomeActivity.class);        startActivity(intent);        finish();    }*/    @Override    protected void onStop() {        super.onStop();    }}